mapper.select:
SELECT id, title, body content, inserted
FROM Board 
WHERE id = #{id}

mapper.update:
UPDATE Board
SET title = #{title},
	body = #{content}
WHERE id = #{id}

mapper.select:
SELECT Count(b.id)
FROM Board b
WHERE b.title LIKE #{title}

SELECT b.id, b.title, b.inserted, COUNT(r.id) replyNum
FROM Board b LEFT JOIN Reply r ON b.id = r.board_id
WHERE b.title LIKE #{title} 
GROUP BY b.id
ORDER BY 1 DESC
LIMIT #{from}, #{rowPerPage}
